const Alert_80 = ({ msg, type, removeAlert}) => {

  useEffect(() => {
    const timeout = setTimeout( () => {
      removeAlert();
    }, 3000);

  });

  return <p className={`alert alert-${type}`}> {msg} </p>
}

export default Alert_80
