import React, { useState, useEffect } from 'react';
import List_80 from './components/List_80';
import Alert_80 from './components/Alert_80';

const getLocalStorage = () => {
  let list = localStorage.getItem('list');
  if(list) {
    return JSON.parse();
  } else {
    return [];   //字串轉成陣列
  } 
}

const App_80 = () => {
  const [name, setName] = useState('');
  const [list, setList] = useState([]);
  const [alert, setAlert] = useState({
    show: false,
    msg: '',
    type: '',
  });

  useEffect( () => {
    localStorage.setItem('list', JSON.stringify(list));  //陣列轉成字串
  }, {list});

  const showAlert = (show = false, msg = '', type = '') => {
    setAlert({show, msg, type });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name) {
      showAlert(true, 'please enter value', 'danger'); 
    } else {
      showAlert(true, 'value changed', 'success');
      const newItem = {
        id: new Date().getTime().toString(),
        title: name
      };
      setList([...list, newItem]);
      setName('')
    }
  };

  return (
    <>
      <section className='section-center'>
        <form className='grocery-form' onSubmit={handleSubmit}>
          { alert.show && <Alert_80 {...alert} removeAlert= {showAlert}/>}
          <h3>Grocery Bud - 208410380</h3>
          <div className='form-control'>
            <input
              type='text'
              className='grocery'
              placeholder='e.g eggs'
              value={name}
              onChange={(e) => {setName(e.target.value)}}
            />
            <button type='submit' className='submit-btn'>
              submit
            </button>
          </div>
        </form>
        { list.length > 0 && (
          <div className="grocery-container">
            <List_80 items={list} />
            <button className="clear-btn">clear items</button>
          </div>
          )}
      </section>
    </>
  );
};

export default App_80;
